import React, { useEffect, useState, useCallback } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import "../styles/pagesstyle.css";

const Dashboard = ({ email }) => {
  const [userData, setUserData] = useState(null);
  const [error, setError] = useState("");
  const navigate = useNavigate();

  const [formData, setFormData] = useState({
    amount: "",
    category: "",
    description: "",
    email: email || ""
  });

  const [deleteId, setDeleteId] = useState("");

  // Memoized fetchUserData to prevent re-creation
  const fetchUserData = useCallback(() => {
    axios.get(`http://localhost:5000/dashboard?email=${email}`)
      .then(response => setUserData(response.data))
      .catch(() => setError("Failed to load data."));
  }, [email]);

  useEffect(() => {
    if (!email) {
      navigate("/");
    }
    fetchUserData();
  }, [email, navigate, fetchUserData]);

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleAddExpense = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post("http://localhost:5000/add-expense", formData);
      alert(response.data.message || "Add Data Successfull!");
      setFormData({ amount: "", category: "", description: "", email: email });
      fetchUserData(); // Refresh data
    } catch (error) {
      console.error("Error adding expense:", error);
      alert("Failed to add expense. Try again!");
    }
  };

  const handleDeleteExpense = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.delete(`http://localhost:5000/delete-expense/${deleteId}`);
      alert(response.data.message);
      setDeleteId("");
      fetchUserData(); // Refresh data
    } catch (error) {
      console.error("Error deleting expense:", error);
      alert("Failed to delete expense. Try again!");
    }
  };

  const handleDownloadPDF = async () => {
    try {
      // Fetch JSON data first
      const response = await fetch(`http://localhost:5000/get-expenses?email=${email}`);

      if (!response.ok) {
        throw new Error("Failed to fetch expenses");
      }

      const data = await response.json();

      // Send JSON to PDF API
      const pdfResponse = await fetch("http://localhost:5000/export-pdf", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(data)  // Send the fetched expenses to generate the PDF
      });

      if (!pdfResponse.ok) {
        throw new Error("Failed to generate PDF");
      }

      // Handle the PDF download
      const blob = await pdfResponse.blob();
      const url = window.URL.createObjectURL(blob);
      const a = document.createElement("a");
      a.href = url;
      a.download = `ExpenseReport_${data.username}.pdf`;
      document.body.appendChild(a);
      a.click();
      document.body.removeChild(a);

    } catch (error) {
      console.error("Error downloading PDF:", error);
    }
  };



  return (
    <div className="dashboard-content">
      {userData ? (
        <>
          <div className="dashboard-nav-container">
            <nav className="dashboard-navbar">
              <div className="dashboard-logo">
                <img src="https://github.com/Sanjeevan1122003/expense-tracker-demo/blob/main/dashboard_logo.png?raw=true" alt="Logo" className="logo" />
              </div>
              <div className="dashboard-buttons">
                <button className="dashboard-button"><a href="#sectionHome">Home</a></button>
                <button className="dashboard-button"><a href="#sectionExpense">Expenses</a></button>
                <button className="dashboard-button"><a href="#sectionData">Your Data</a></button>
              </div>
            </nav>
          </div>
          {error && <p className="error-message">{error}</p>}
          <section id="sectionHome">
            <div className="home-content">
              <div className="home-container">
                <div className="home-card">
                  <div className="home-card-description">
                    <h1 className="home-title">Hi, <span className="highlights">{userData.username}</span></h1>
                    <h2 className="home-title">Welcome to <span className="highlights">Expense Tracker</span> Dashboard</h2>
                    <h3 className="home-title">Track your Expenses Easily</h3>
                    <p className="home-description">Manage your daily expenses efficiently and take control of your
                      financial health.</p>
                    <ul className="home-description">
                      <li>➕ Add your daily expenses</li>
                      <li>📈 View spending insights</li>
                      <li>💰 Set monthly budgets</li>
                      <li>🔔 Get expense alerts</li>
                    </ul>
                  </div>
                  <div className="home-card-image">
                    <img src="https://github.com/Sanjeevan1122003/expense-tracker-demo/blob/main/expence_tracker_home_page.png?raw=true" alt="Logo" className="home-image" />
                  </div>
                </div>
              </div>
            </div>
          </section>

          <section id="sectionExpense">
            <div className="expense-content">
              <div className="expense-cards">
                <div className="expense-card">
                  <p className="expense-card-title">Add Expense</p>
                  <div className="expense-card-from">
                    <form onSubmit={handleAddExpense}>
                      <label htmlFor="amount">Enter the cost(amount): </label><br></br>
                      <input type="number" name="amount" placeholder="Enter amount" required value={formData.amount} onChange={handleChange} />
                      <label htmlFor="select">Category(income / expense): </label><br></br>
                      <select name="category" required value={formData.category} onChange={handleChange}>
                        <option value="">Select Category</option>
                        <option value="Income">Income</option>
                        <option value="Expense">Expense</option>
                      </select><br></br>
                      <label htmlFor="description">Enter the description(detail of data): </label><br></br>
                      <input type="text" name="description" placeholder="Enter details" value={formData.description} onChange={handleChange} />
                      <p><strong className="highlights">Note:</strong> Once you add the data you can't change it, So fill the details carefully</p>
                      <div className="button-container button-add">
                        <button type="submit">Add Expense</button>
                      </div>
                    </form>
                  </div>
                </div>
                <div className="expense-card">
                  <p className="expense-card-title">Delete Expense</p>
                  <div className="expense-card-from">
                    <form onSubmit={handleDeleteExpense}>
                      <label htmlFor="id">Enter the expense Id:</label><br></br>
                      <input type="number" name="id" placeholder="Enter Expense ID" required value={deleteId} onChange={(e) => setDeleteId(e.target.value)} />
                      <p>You can see the your data in the below table and get Id for the data you want to delete</p>
                      <p><strong className="highlights">Note:</strong> Once you delete the data you can't recover it, So fill the detail carefully</p>
                      <div className="button-container button-delete">
                        <button type="submit">Delete Expense</button>
                      </div>
                    </form>
                  </div>
                </div>
              </div>
            </div>
          </section>

          <section id="sectionData">
            <div className="data-content">
              <div className="data-container">
                <div className="data-cards">
                  <div className="data-top-cards">
                    <div className="data-top-card">
                      <h3>Total Income: ₹<b className="highlights">{userData.totalIncome}</b></h3>
                    </div>
                    <div className="data-top-card">
                      <h3>Total Expense: <b className="highlights" >₹ {userData.totalExpense}</b></h3>
                    </div>
                    <div className="data-top-card">
                      <button onClick={handleDownloadPDF}>Download PDF</button>
                    </div>
                  </div>
                  <div className="data-bottom-card">
                    <table className="data-table">
                      <thead>
                        <tr>
                          <th>ID</th>
                          <th>Category</th>
                          <th>Description</th>
                          <th>Amount</th>
                        </tr>
                      </thead>
                      <tbody>
                        {userData.expenses.map((exp) => (
                          <tr key={exp.id}>
                            <td>{exp.id}</td>
                            <td>{exp.category}</td>
                            <td>{exp.description}</td>
                            <td>{exp.amount}</td>
                          </tr>
                        ))}
                      </tbody>
                    </table>
                  </div>
                </div>
              </div>
            </div>
          </section>

          <div className="footer">
            <p>&copy; 2025 Expense Tracker</p>
          </div>
        </>
      ) : <p>Loading...</p>}
    </div>
  );
};

export default Dashboard;
