import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import "../styles/pagesstyle.css";

const Dashboard = () => {
  const navigate = useNavigate();
  const [formData, setFormData] = useState({
    amount: "",
    category: "",
    description: "",
  });
  const [email, setEmail] = useState("");
  const [expenses, setExpenses] = useState([]);
  const [totalIncome, setTotalIncome] = useState(0);
  const [totalExpense, setTotalExpense] = useState(0);
  const [deleteId, setDeleteId] = useState("");

  useEffect(() => {
    const storedEmail = localStorage.getItem("email");
    if (!storedEmail) {
      navigate("/login");
    } else {
      setEmail(storedEmail);
      fetchExpenses(storedEmail);
    }
  }, [navigate]);

  const fetchExpenses = async (email) => {
    try {
      const response = await fetch(`http://localhost:5000/expenses?email=${email}`);
      const data = await response.json();
      if (response.ok) {
        setExpenses(data.expenses);
        setTotalIncome(data.totalIncome);
        setTotalExpense(data.totalExpense);
      } else {
        alert("Failed to fetch expenses");
      }
    } catch (error) {
      console.error("Error:", error);
    }
  };

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await fetch("http://localhost:5000/add-expense", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ ...formData, email }),
      });

      if (response.ok) {
        alert("Expense added successfully!");
        fetchExpenses(email);
      } else {
        alert("Failed to add expense");
      }
    } catch (error) {
      console.error("Error:", error);
    }
  };

  const handleDelete = async (e) => {
    e.preventDefault();
    try {
      const response = await fetch(`http://localhost:5000/delete-expense/${deleteId}`, {
        method: "DELETE",
      });

      if (response.ok) {
        alert("Expense deleted successfully!");
        fetchExpenses(email);
      } else {
        alert("Failed to delete expense");
      }
    } catch (error) {
      console.error("Error:", error);
    }
  };

  return (
    <div className="dashboard-content">
      <nav className="dashboard-navbar">
        <div className="dashboard-logo">
          <img src="https://github.com/Sanjeevan1122003/expense-tracker-demo/blob/main/dashboard_logo.png?raw=true" alt="Logo" className="logo" />
        </div>
        <div className="dashboard-buttons">
          <button className="dashboard-button"><a href="#sectionHome">Home</a></button>
          <button className="dashboard-button"><a href="#sectionExpense">Expenses</a></button>
          <button className="dashboard-button"><a href="#sectionData">Your Data</a></button>
        </div>
      </nav>

      <section id="sectionHome">
        <div className="home-content">
          <h1>Hi, <span className="highlights">{email}</span></h1>
          <h2>Welcome to <span className="highlights">Expense Tracker</span> Dashboard</h2>
          <p>Manage your expenses easily.</p>
        </div>
      </section>

      <section id="sectionExpense">
        <div className="expense-content">
          <form onSubmit={handleSubmit}>
            <input type="number" name="amount" placeholder="Enter amount" required onChange={handleChange} />
            <select name="category" required onChange={handleChange}>
              <option value="">Select Category</option>
              <option value="Income">Income</option>
              <option value="Expense">Expense</option>
            </select>
            <input type="text" name="description" placeholder="Enter details" onChange={handleChange} />
            <button type="submit">Add Expense</button>
          </form>

          <form onSubmit={handleDelete}>
            <input type="number" placeholder="Enter Expense ID" required onChange={(e) => setDeleteId(e.target.value)} />
            <button type="submit">Delete Expense</button>
          </form>
        </div>
      </section>

      <section id="sectionData">
        <div className="data-content">
          <h3>Total Income: {totalIncome}</h3>
          <h3>Total Expense: {totalExpense}</h3>
          <a href="http://localhost:5000/export-pdf?email=${email}" download="ExpenseData.pdf">Download PDF</a>

          <table>
            <thead>
              <tr>
                <th>ID</th>
                <th>Amount</th>
                <th>Category</th>
                <th>Description</th>
              </tr>
            </thead>
            <tbody>
              {expenses.map((expense) => (
                <tr key={expense.id}>
                  <td>{expense.id}</td>
                  <td>{expense.amount}</td>
                  <td>{expense.category}</td>
                  <td>{expense.description}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </section>
    </div>
  );
};

export default Dashboard;
